sp_widget.client_script:
function($scope, $rootScope,$timeout, $uibModal, spUtil, spModal) {
  /* widget controller */
  var c = this;
	c.show_recent = false;

	c.KEYS = {
						DOWN:40,
						LEFT:37,
						RIGHT:39,
						TAB:9,
						UP:38
					 }

	if (c.data.recentItems.length > 0)
		c.show_recent = true;

	c.changePanel = function(panel_name) {
		if (c.show_recent) {
			if (panel_name == 'popular')
				c.show_recent = false;
		} else {
			if (panel_name == 'recent')
				c.show_recent = true;
		}
	};

	c.switchTab = function($event, tab) {
		var key = null;
		if (event.keyCode === c.KEYS.LEFT || event.keyCode === c.KEYS.UP)
			key = c.KEYS.LEFT;
		else if (event.keyCode === c.KEYS.RIGHT || event.keyCode === c.KEYS.DOWN)
			key = c.KEYS.RIGHT;
		else if (event.keyCode === c.KEYS.TAB) {
			return;
		}
		if (key === null)
			return;

		c.show_recent = !c.show_recent;
		if (c.show_recent)
			$('a.recent').focus();
		else
			$('a.popular').focus();
	}

	c.ideaModal = function(ev, sysId){
		//alert('Event=' + ev + '    Campaign=' + sysId);
		// Create Instance Of The Idea Record Producer Widget
		spUtil.get('widget-sc-cat-item-tabbed-bae', {sysparm_campaign: sysId, sys_id: '480589a2dbba2780561fb29a689619da'}).then(function(response){
			c.ideaModalInstance = response;
			c.ideaModalInstance.options = {"show_submit_order_button": true, "default_fields": {'campaign' : sysId}};
		});
		// Open the Idea Modal
		$uibModal.open({
			templateUrl: 'ideaModalTemplate',
			size : 'lg',
			scope: $scope
		});		
	};    

	function _preventDefault(evt){
		if(evt){
			evt.stopPropagation();
			evt.preventDefault();
		}
	}	

}

