sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//Adjust parent Idea & Campaign vote count, based on the type of vote inserted/deleted
	var parIdea = new GlideRecord("x_baes2_busimprove_ideas");
	var parCampaign = new GlideRecord("x_baes2_busimprove_campaigns");
	if (parIdea.get(current.idea.sys_id.toString()) && parCampaign.get(current.idea.campaign.sys_id.toString())) {
		var voteStyle = current.type;
		if (current.operation() == 'delete'){
			if (voteStyle == 'upvote'){
				parIdea.vote_count--;
				parIdea.total_vote_count--;
				parIdea.update();
				parCampaign.vote_count--;
				parCampaign.total_vote_count--;
				parCampaign.update();				
			}
			else if (voteStyle == 'downvote'){
				parIdea.vote_count++;
				parIdea.total_vote_count--;
				parIdea.update();
				parCampaign.vote_count++;
				parCampaign.total_vote_count--;
				parCampaign.update();				
			}
		}
		else if (current.operation() == 'insert'){
			if (voteStyle == 'upvote'){
				parIdea.vote_count++;
				parIdea.total_vote_count++;
				parIdea.update();
				parCampaign.vote_count++;
				parCampaign.total_vote_count++;
				parCampaign.update();				
			}
			else if (voteStyle == 'downvote'){
				parIdea.vote_count--;
				parIdea.total_vote_count++;
				parIdea.update();
				parCampaign.vote_count--;
				parCampaign.total_vote_count++;
				parCampaign.update();				
			}
		}
	}	

})(current, previous);
